(544, 16, 5, 29, NULL, NULL, NULL, '2023-02-10', '201', 'Deputy Director Human Resources', 'FOD', 'Head Office', 'Operations', 'Kiambere Field Report of 23rd  to 5th January 2023', 'Major', '<ul><li>Security guard riding motorbike without riding gear&nbsp; while taking random calls</li></ul>', 'closed', '2023-03-06', '2023-03-06', NULL, NULL, NULL, NULL, '<div>This is being addressed by HR through training and workers will sign.</div>', '', '', '<div>This is being addressed by HR through training and workers will sign.</div>', '2023-02-10 14:09:03', '2023-05-11 15:31:37'),
(545, 16, 8, 9, NULL, NULL, NULL, '2023-02-10', '2749', 'Deputy Director Human Resources', 'Executive Director Forestry', 'Head Office', 'Forestry', 'Kiambere Field Report of 23rd  to 5th January 2023', 'Major', '<ol><li>Staff drinking water:&nbsp;<br>&nbsp; &nbsp; &nbsp; a)Second water tank with drinking being uncovered for some days.&nbsp;<br>&nbsp; &nbsp; &nbsp; b)Spilling of drinking water when PMS are fetching for their field activity in the morning</li></ol>', 'closed', '2023-03-06', '2023-03-08', NULL, NULL, NULL, NULL, '<div>This has been solved by extending the pipe with tap and fenced.</div>', '', '', '<div>This has been solved by extending the pipe with tap and fenced.</div>', '2023-02-10 14:18:44', '2023-05-11 15:32:43'),
(546, 16, 5, 29, NULL, NULL, NULL, '2023-02-10', '4375', 'Deputy Director Human Resources', 'FOD', 'Head Office', 'Operations', 'Kiambere Field Report of 23rd  to 5th  January 2023', 'Major', '<ul><li>Crocodile cage is in bad shape. Its detached in most of points/joints</li></ul>', 'closed', '2023-03-06', '2023-03-06', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2023-02-10 14:20:19', '2023-05-11 15:34:07'),
(547, 16, 5, 29, NULL, NULL, NULL, '2023-02-10', '126', 'Deputy Director Human Resources', 'FOD', 'Head Office', 'Operations', 'Kiambere Field Report of 23rd  to 5th  January 2023', 'Major', '<ul><li>Japheth Mutemi (Assistant HOS) and FT handling metallic equipment for repair without gloves</li></ul>', 'closed', '2023-03-06', '2023-03-06', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2023-02-10 14:26:10', '2023-05-11 15:35:02'),
